#import "ViewController.h"
#import <WebKit/WebKit.h>

@interface ViewController ()<WKUIDelegate,WKNavigationDelegate>
@property (nonatomic, strong) WKWebView *webview;
@end

@implementation ViewController


- (void)viewDidLoad {
    [super viewDidLoad];
    self.view.backgroundColor = [UIColor whiteColor];
    WKWebViewConfiguration *config = [[WKWebViewConfiguration alloc] init];
    _webview = [[WKWebView alloc] initWithFrame:self.view.bounds configuration:config];
    _webview.UIDelegate = self;
    _webview.navigationDelegate = self;
    [self.view addSubview:_webview];
    
    NSString *htmlString = [NSString stringWithFormat:@"<html> \n"
                            "<head> \n"
                            "<style type=\"text/css\"> \n"
                            "body {font-size:30px;}\n"
                            "</style> \n"
                            "</head> \n"
                            "<body style=\"word-wrap:break-word; font-family:Arial\">"
                            "<script type='text/javascript'>"
                            "window.onload = function(){\n"
                            "var $img = document.getElementsByTagName('img');\n"
                            "for(var p in  $img){\n"
                            "$img[p].style.width = '100%%';\n"
                            "$img[p].style.height ='auto'\n"
                            "$img[p].style.align ='center'\n"
                            "}\n"
                            "}"
                            "</script>%@"
                            "</body>"
                            "</html>", kHTML];
    
    
    NSData *data = [htmlString dataUsingEncoding:NSUTF8StringEncoding];
    
    
    
    
    [self.webview loadData:data MIMEType:@"text/html" characterEncodingName:@"UTF-8" baseURL:[NSURL fileURLWithPath:NSTemporaryDirectory()]];

    char name[] = {'a', 'b', 'c'};
    unsigned int count = sizeof(name)/sizeof(char);
    printf("%d", count);
}


- (void)webView:(WKWebView *)webView didFinishNavigation:(WKNavigation *)navigation
{
    [webView evaluateJavaScript:@"document.documentElement.style.webkitUserSelect='none';" completionHandler:nil];
    [webView evaluateJavaScript:@"document.activeElement.blur();" completionHandler:nil];
    // 适当增大字体大小
    [webView evaluateJavaScript:@"document.getElementsByTagName('body')[0].style.webkitTextSizeAdjust= '105%'" completionHandler:nil];
}


- (void)webView:(WKWebView *)webView decidePolicyForNavigationAction:(WKNavigationAction *)navigationAction decisionHandler:(void (^)(WKNavigationActionPolicy))decisionHandler
{
    NSMutableURLRequest *request = [navigationAction.request mutableCopy];
    NSLog(@"request————————>%@",request.URL.absoluteString);
    
    if ([request.URL.absoluteString containsString:@"blank"]) {
        decisionHandler(WKNavigationActionPolicyCancel);
        return;
    }
    decisionHandler(WKNavigationActionPolicyAllow);
}

@end


